import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title SimpleNativeMarket
 * @notice Simple prediction market using native TTRUST
 */
contract SimpleNativeMarket is Ownable {

    enum PredictionType {
        MARKET_CAP_ABOVE,
        MARKET_CAP_BELOW,
        PRICE_ABOVE,
        PRICE_BELOW
    }

    // Market details
    PredictionType public predictionType;
    uint256 public targetValue;
    uint256 public deadline;
    string public title;
    string public predictionQuestion;
    address public creator;

    // Market state
    uint256 public totalLiquidity;
    uint256 public yesPool;
    uint256 public noPool;
    bool public isResolved;
    bool public targetReached;

    // User positions (in TTRUST wei)
    mapping(address => uint256) public yesPositions;
    mapping(address => uint256) public noPositions;

    // Events
    event YesPurchased(address indexed buyer, uint256 amount, uint256 ttrust);
    event NoPurchased(address indexed buyer, uint256 amount, uint256 ttrust);
    event MarketResolved(bool targetReached);
    event Redeemed(address indexed user, uint256 ttrust);

    constructor(
        address _creator,
        PredictionType _predictionType,
        uint256 _targetValue,
        uint256 _deadline,
        string memory _title,
        string memory _predictionQuestion
    ) payable Ownable(_creator) {
        creator = _creator;
        predictionType = _predictionType;
        targetValue = _targetValue;
        deadline = _deadline;
        title = _title;
        predictionQuestion = _predictionQuestion;

        // Initialize pools with sent TTRUST
        totalLiquidity = msg.value;
        yesPool = msg.value / 2;  // 50/50 initial split
        noPool = msg.value / 2;

        require(msg.value > 0, "Must provide initial liquidity");
    }

    /**
     * @notice Buy YES tokens with native TTRUST
     */
    function buyYes() external payable {
        require(msg.value > 0, "Must send TTRUST");
        require(block.timestamp < deadline, "Market expired");
        require(!isResolved, "Market resolved");

        uint256 yesToBuy = calculateYesToBuy(msg.value);
        require(yesToBuy > 0, "Invalid purchase amount");

        yesPositions[msg.sender] += yesToBuy;
        yesPool += msg.value;

        emit YesPurchased(msg.sender, yesToBuy, msg.value);
    }

    /**
     * @notice Buy NO tokens with native TTRUST  
     */
    function buyNo() external payable {
        require(msg.value > 0, "Must send TTRUST");
        require(block.timestamp < deadline, "Market expired");
        require(!isResolved, "Market resolved");

        uint256 noToBuy = calculateNoToBuy(msg.value);
        require(noToBuy > 0, "Invalid purchase amount");

        noPositions[msg.sender] += noToBuy;
        noPool += msg.value;

        emit NoPurchased(msg.sender, noToBuy, msg.value);
    }

    /**
     * @notice Simple AMM pricing for YES tokens
     */
    function calculateYesToBuy(uint256 ttrust) public view returns (uint256) {
        if (yesPool == 0) return ttrust;
        // Simple formula: more expensive as pool grows
        return (ttrust * 1e18) / (1e18 + yesPool * 1e18 / (yesPool + noPool));
    }

    /**
     * @notice Simple AMM pricing for NO tokens  
     */
    function calculateNoToBuy(uint256 ttrust) public view returns (uint256) {
        if (noPool == 0) return ttrust;
        // Simple formula: more expensive as pool grows
        return (ttrust * 1e18) / (1e18 + noPool * 1e18 / (yesPool + noPool));
    }

    /**
     * @notice Get current YES price (TTRUST per YES token)
     */
    function getYesPrice() external view returns (uint256) {
        if (yesPool == 0) return 1e18; // 1 TTRUST
        return (yesPool * 1e18) / (yesPool + noPool);
    }

    /**
     * @notice Get current NO price (TTRUST per NO token)
     */
    function getNoPrice() external view returns (uint256) {
        if (noPool == 0) return 1e18; // 1 TTRUST  
        return (noPool * 1e18) / (yesPool + noPool);
    }

    /**
     * @notice Resolve market (only owner/oracle)
     */
    function resolveMarket(bool _targetReached) external onlyOwner {
        require(block.timestamp >= deadline, "Market not expired");
        require(!isResolved, "Already resolved");

        isResolved = true;
        targetReached = _targetReached;

        emit MarketResolved(_targetReached);
    }

    /**
     * @notice Redeem winning positions
     */
    function redeem() external {
        require(isResolved, "Market not resolved");

        uint256 payout = 0;

        if (targetReached && yesPositions[msg.sender] > 0) {
            // YES won - calculate payout from total pool
            payout = (yesPositions[msg.sender] * (yesPool + noPool)) / getTotalYesTokens();
            yesPositions[msg.sender] = 0;
        } else if (!targetReached && noPositions[msg.sender] > 0) {
            // NO won - calculate payout from total pool
            payout = (noPositions[msg.sender] * (yesPool + noPool)) / getTotalNoTokens();
            noPositions[msg.sender] = 0;
        }

        require(payout > 0, "No payout available");

        payable(msg.sender).transfer(payout);
        emit Redeemed(msg.sender, payout);
    }

    /**
     * @notice Get total YES tokens issued (placeholder)
     */
    function getTotalYesTokens() public view returns (uint256) {
        return yesPool; // Simplified for now
    }

    /**
     * @notice Get total NO tokens issued (placeholder)  
     */
    function getTotalNoTokens() public view returns (uint256) {
        return noPool; // Simplified for now
    }

    /**
     * @notice Get market info
     */
    function getMarketInfo() external view returns (
        string memory,
        string memory, 
        uint256,
        uint256,
        uint256,
        uint256,
        bool,
        bool
    ) {
        return (
            title,
            predictionQuestion,
            targetValue,
            deadline, 
            yesPool,
            noPool,
            isResolved,
            targetReached
        );
    }

    /**
     * @notice Emergency receive function
     */
    receive() external payable {
        // Allow contract to receive TTRUST
    }
}","keccak256":"0x568a35ff2f76b28d64d6e1161eaf265a4b602dfc23a7b6ab37755548cb235fc9","license":"MIT"},"contracts/TTrustPriceOracle.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { AggregatorV3Interface } from "./interfaces/AggregatorV3Interface.sol";

/**
 * @title TTrustPriceOracle
 * @notice Oracle contract to get TTrust price data from multiple sources
 * @dev Can integrate with DEX prices, external oracles, or manual price feeds
 */
contract TTrustPriceOracle is Ownable {

    /////////////////
    /// Errors //////
    /////////////////

    error TTrustPriceOracle__PriceStale();
    error TTrustPriceOracle__InvalidPrice();
    error TTrustPriceOracle__OnlyTrustedUpdater();
    error TTrustPriceOracle__InvalidDEXAddress();

    //////////////////////////
    /// State Variables //////
    //////////////////////////

    struct PriceData {
        uint256 price;          // Price in USD (18 decimals)
        uint256 timestamp;      // Last update timestamp
        uint256 marketCap;      // Market cap in USD (18 decimals)
        bool isValid;           // Is this price valid
    }

    PriceData public currentPrice;

    uint256 public constant PRICE_STALENESS_THRESHOLD = 1 hours;
    uint256 public constant PRECISION = 1e18;

    // Trusted price updaters (can be set to external oracles or bots)
    mapping(address => bool) public trustedUpdaters;

    // DEX contract address for getting prices from liquidity pools
    address public dexRouter;
    address public ttustIntuitPair;

    // Backup price feeds (Chainlink style)
    AggregatorV3Interface public backupPriceFeed;

    /////////////////////////
    /// Events //////
    /////////////////////////

    event PriceUpdated(uint256 indexed price, uint256 indexed marketCap, uint256 timestamp, address updater);
    event TrustedUpdaterSet(address indexed updater, bool trusted);
    event DEXRouterSet(address indexed router, address indexed pair);
    event BackupPriceFeedSet(address indexed priceFeed);

    /////////////////
    /// Modifiers ///
    /////////////////

    modifier onlyTrustedUpdater() {
        if (!trustedUpdaters[msg.sender] && msg.sender != owner()) {
            revert TTrustPriceOracle__OnlyTrustedUpdater();
        }
        _;
    }

    //////////////////
    ////Constructor///
    //////////////////

    constructor(
        address _owner,
        uint256 _initialPrice,
        uint256 _initialMarketCap
    ) Ownable(_owner) {
        // Set initial price data
        currentPrice = PriceData({
            price: _initialPrice,
            timestamp: block.timestamp,
            marketCap: _initialMarketCap,
            isValid: true
        });

        // Owner is automatically a trusted updater
        trustedUpdaters[_owner] = true;

        emit PriceUpdated(_initialPrice, _initialMarketCap, block.timestamp, msg.sender);
    }

    /////////////////
    /// Functions ///
    /////////////////

    /**
     * @notice Update TTrust price manually (for trusted updaters)
     * @param _price New price in USD (18 decimals)
     * @param _marketCap New market cap in USD (18 decimals)
     */
    function updatePrice(uint256 _price, uint256 _marketCap) 
        external 
        onlyTrustedUpdater 
    {
        if (_price == 0) {
            revert TTrustPriceOracle__InvalidPrice();
        }

        currentPrice = PriceData({
            price: _price,
            timestamp: block.timestamp,
            marketCap: _marketCap,
            isValid: true
        });

        emit PriceUpdated(_price, _marketCap, block.timestamp, msg.sender);
    }

    /**
     * @notice Get current TTrust price
     * @return price Current price in USD (18 decimals)
     * @return timestamp Last update timestamp
     */
    function getPrice() external view returns (uint256 price, uint256 timestamp) {
        if (!isPriceValid()) {
            revert TTrustPriceOracle__PriceStale();
        }

        return (currentPrice.price, currentPrice.timestamp);
    }

    /**
     * @notice Get current TTrust market cap
     * @return marketCap Current market cap in USD (18 decimals)
     * @return timestamp Last update timestamp
     */
    function getMarketCap() external view returns (uint256 marketCap, uint256 timestamp) {
        if (!isPriceValid()) {
            revert TTrustPriceOracle__PriceStale();
        }

        return (currentPrice.marketCap, currentPrice.timestamp);
    }

    /**
     * @notice Check if current price is valid (not stale)
     * @return bool True if price is valid
     */
    function isPriceValid() public view returns (bool) {
        return currentPrice.isValid && 
               (block.timestamp - currentPrice.timestamp) <= PRICE_STALENESS_THRESHOLD;
    }

    /**
     * @notice Get price from DEX (if configured)
     * @return price Price from DEX
     */
    function getPriceFromDEX() external view returns (uint256 price) {
        // This would integrate with the existing DEX to get TTrust price
        // Implementation depends on the DEX structure
        if (dexRouter == address(0)) {
            return 0;
        }

        // TODO: Implement actual DEX price fetching
        // For now, return current price as fallback
        return currentPrice.price;
    }

    /////////////////////////
    /// Admin Functions ////
    /////////////////////////

    /**
     * @notice Set trusted updater status
     * @param _updater Address to set trust status for
     * @param _trusted Whether this address is trusted
     */
    function setTrustedUpdater(address _updater, bool _trusted) external onlyOwner {
        trustedUpdaters[_updater] = _trusted;
        emit TrustedUpdaterSet(_updater, _trusted);
    }

    /**
     * @notice Set DEX router and pair addresses for price fetching
     * @param _router DEX router address
     * @param _pair TTrust/INTUIT pair address
     */
    function setDEXAddresses(address _router, address _pair) external onlyOwner {
        if (_router == address(0) || _pair == address(0)) {
            revert TTrustPriceOracle__InvalidDEXAddress();
        }

        dexRouter = _router;
        ttustIntuitPair = _pair;

        emit DEXRouterSet(_router, _pair);
    }

    /**
     * @notice Set backup price feed (Chainlink style)
     * @param _priceFeed Chainlink price feed address
     */
    function setBackupPriceFeed(address _priceFeed) external onlyOwner {
        backupPriceFeed = AggregatorV3Interface(_priceFeed);
        emit BackupPriceFeedSet(_priceFeed);
    }

    /**
     * @notice Emergency function to invalidate current price
